import React, { Component } from "react";
import ReactDOM from "react-dom";
import "./index.css";

class Square extends Component {
  render() {
    return (
      <button
        className={"square squareID-" + this.props.squareId}
        // onClick={() => {
        //   this.props.onClick();
        // }}
        onClick={this.props.onClick}
      >
        {this.props.value}
      </button>
    );
  }
}

// function Square(props) {
//   return (
//     <button className="square" onClick={props.onClick}>
//       {props.value}
//     </button>
//   );
// }

class Board extends Component {
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(9).fill(null),
      xIsNext: true
    };
    this.handleClick = this.handleClick.bind(this); //this function is modifying the state
  }

  handleClick(event) {
    //
    const a = event.target.className;
    const i = a.slice(a.indexOf("squareID-") + 9);
    // debugger;
    //
    const squares = this.state.squares;
    /* determine if the square was already filled */
    if (squares[i] === null) {
      /* determine who is next */
      // const xIsNext = this.state.xIsNext;
      // if (xIsNext) {
      //   squares[i] = "X";
      // } else {
      //   squares[i] = "O";
      // }
      // this.setState({ xIsNext: !xIsNext });
      // this.setState(squares);
      squares[i] = this.state.xIsNext ? "X" : "O";
      this.setState({
        squares,
        xIsNext: !this.state.xIsNext
      });
    } else {
      alert("this spot was filled already");
    }
  }

  renderSquare(i) {
    return (
      <Square
        squareId={i}
        value={this.state.squares[i]}
        onClick={this.handleClick}
        // onClick={() => {
        //   this.handleClick(i);
        // }}                 // every time
        // onClick={this.handleClick}
      />
    );
  }

  render() {
    const status = "Next player: X";

    const a = [0, 1, 2].map((d, index) => {
      return (
        <div className="board-row">
          {this.renderSquare(index * 3)}
          {this.renderSquare(index * 3 + 1)}
          {this.renderSquare(index * 3 + 2)}
        </div>
      );
    });

    debugger;
    return (
      <div>
        <div className="status">{status}</div>
        {/* <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div> */}

        {[0, 1, 2].map((d, index) => {
          return (
            <div className="board-row" key={index}>
              {this.renderSquare(index * 3)}
              {this.renderSquare(index * 3 + 1)}
              {this.renderSquare(index * 3 + 2)}
            </div>
          );
        })}
      </div>
    );
  }
}

class Game extends Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}

/* determine winner */
function determineWinner(squares) {
  const winMatrix = ((0, 1, 2),
  (3, 4, 5),
  (6, 7, 8),
  (0, 3, 6),
  (1, 4, 7),
  (2, 5, 8),
  (0, 4, 8),
  (2, 4, 6));
  // get X's array
  const xArray = [];
  squares.map((value, index) => {
    if (value === "X") {
      xArray.push(index);
    }
  });
  console.log(xArray);
}

// ========================================

ReactDOM.render(<Game />, document.getElementById("root"));
